import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';

class WholeSite extends React.Component {
	render() {
		return (
			<React.Fragment>
				<div id="spotifyLogin">
					<SpotifyLogin />
				</div>
				<div id="playlistBuilder">
					<PlaylistBuilder />
				</div>
			</React.Fragment>
			//<SpotifyLogin />
		);
	}
}

class PlaylistBuilder extends React.Component {
	constructor(props) {
		super(props);
		this.state = {mainArtistTracks:[],
					mainArtistSimilarArtists:[],
					currentMainArtist:"",
					mainArtistData:{},
					spiderLeg:[{"left": "10%", "top": "10%", "artistName": "", "artistID": 0},
								{"left": "30%", "top": "5%", "artistName": "", "artistID": 0},
								{"left": "60%", "top": "12%", "artistName": "", "artistID": 0},
								{"left": "70%", "top": "30%", "artistName": "", "artistID": 0},
								{"left": "75%", "top": "48%", "artistName": "", "artistID": 0},
								{"left": "13%", "top": "30%", "artistName": "", "artistID": 0},
								{"left": "40%", "top": "20%", "artistName": "", "artistID": 0},
								{"left": "50%", "top": "30%", "artistName": "", "artistID": 0},
								{"left": "78%", "top": "8%", "artistName": "", "artistID": 0},
								{"left": "68%", "top": "60%", "artistName": "", "artistID": 0},
								{"left": "72%", "top": "68%", "artistName": "", "artistID": 0},
								{"left": "65%", "top": "90%", "artistName": "", "artistID": 0},
								{"left": "80%", "top": "76%", "artistName": "", "artistID": 0}
								]
					};
		this.mainArtistChange = this.mainArtistChange.bind(this);
		this.mainArtistSubmit = this.mainArtistSubmit.bind(this);
		this.searchAPI = this.searchAPI.bind(this);
		this.artistTopTracksAPI = this.artistTopTracksAPI.bind(this);
		this.artistSimilarArtistsAPI = this.artistSimilarArtistsAPI.bind(this);
		this.artistGetAllData = this.artistGetAllData.bind(this);
		this.processArtistData = this.processArtistData.bind(this);
		this.setNewArtist = this.setNewArtist.bind(this);
	}
	
	mainArtistChange(mainArtistVal) {
		this.setState({currentMainArtist: mainArtistVal});
	}
	
	mainArtistSubmit(mainArtistVal) {
		this.searchAPI(this.state.currentMainArtist)
	}
	
	
	
	searchAPI(searchTerm) {
		fetch("https://api.spotify.com/v1/search?q="+encodeURIComponent(searchTerm)+"&type=artist&limit=5", {
			method: 'get',
			headers: {
				'Authorization': 'Bearer ' + getHashParam("access_token")
			},
			json: true
		}).then(response => response.json()).then((response) => {this.processArtistData(response, 0);});
	}
	
	artistSimilarArtistsAPI(id) {
		fetch("https://api.spotify.com/v1/artists/"+encodeURIComponent(id)+"/related-artists?market=from_token", {
			method: 'get',
			headers: {
				'Authorization': 'Bearer ' + getHashParam("access_token")
			},
			json: true
		}).then(response => response.json()).then((response) => {this.setState({mainArtistSimilarArtists: response["artists"],
																spiderLeg: [{"left": "10%", "top": "10%", "artistName": response["artists"][0]["name"], "artistID": response["artists"][0]["id"]},
																			{"left": "30%", "top": "5%", "artistName": response["artists"][1]["name"], "artistID": response["artists"][1]["id"]},
																			{"left": "60%", "top": "12%", "artistName": response["artists"][2]["name"], "artistID": response["artists"][2]["id"]},
																			{"left": "70%", "top": "30%", "artistName": response["artists"][3]["name"], "artistID": response["artists"][3]["id"]},
																			{"left": "75%", "top": "48%", "artistName": response["artists"][4]["name"], "artistID": response["artists"][4]["id"]},
																			{"left": "13%", "top": "30%", "artistName": response["artists"][5]["name"], "artistID": response["artists"][5]["id"]},
																			{"left": "40%", "top": "20%", "artistName": response["artists"][6]["name"], "artistID": response["artists"][6]["id"]},
																			{"left": "50%", "top": "30%", "artistName": response["artists"][7]["name"], "artistID": response["artists"][7]["id"]},
																			{"left": "78%", "top": "8%", "artistName": response["artists"][8]["name"], "artistID": response["artists"][8]["id"]},
																			{"left": "68%", "top": "60%", "artistName": response["artists"][9]["name"], "artistID": response["artists"][9]["id"]},
																			{"left": "72%", "top": "68%", "artistName": response["artists"][10]["name"], "artistID": response["artists"][10]["id"]},
																			{"left": "75%", "top": "90%", "artistName": response["artists"][11]["name"], "artistID": response["artists"][11]["id"]},
																			{"left": "80%", "top": "76%", "artistName": response["artists"][12]["name"], "artistID": response["artists"][12]["id"]}]});});
	}
	
	setNewArtist(id, name) {
		this.setState({currentMainArtist: name});
		this.artistGetAllData(id);
	}
	
	artistTopTracksAPI(id) {
		fetch("https://api.spotify.com/v1/artists/"+encodeURIComponent(id)+"/top-tracks?market=from_token", {
			method: 'get',
			headers: {
				'Authorization': 'Bearer ' + getHashParam("access_token")
			},
			json: true
		}).then(response => response.json()).then(response => {this.setState({mainArtistTracks: response["tracks"]});});
	}
	
	artistGetAllData(id) {
		this.artistSimilarArtistsAPI(id);
		this.artistTopTracksAPI(id);
	}
	
	processArtistData(response, n) {
		this.setState({mainArtistSearchData: response["artists"]["items"], currentMainArtist: response["artists"]["items"][0]["name"]});
		this.artistGetAllData(response["artists"]["items"][n]["id"]);
	}
	
	renderSpiderLeg(i) {
		return (
      <SpiderLeg
        info={this.state.spiderLeg[i]}
		setNewArtist={this.setNewArtist}
      />
    );
  }
	
	render() {
		const currentMainArtist = this.state.currentMainArtist;
		const mainArtistTracks = this.state.mainArtistTracks;
		
		return (
			<div style={{float: "right"}}>
			<MainArtistForm currentMainArtist={currentMainArtist} 
			mainArtistTracks={mainArtistTracks}
			mainArtistChange={this.mainArtistChange}
			mainArtistSubmit={this.mainArtistSubmit}
			currentSearch={this.state.currentMainArtist}
			/>
			<div>
			{this.renderSpiderLeg(0)}
			{this.renderSpiderLeg(1)}
			{this.renderSpiderLeg(2)}
			{this.renderSpiderLeg(3)}
			{this.renderSpiderLeg(4)}
			{this.renderSpiderLeg(5)}
			{this.renderSpiderLeg(6)}
			{this.renderSpiderLeg(7)}
			{this.renderSpiderLeg(8)}
			{this.renderSpiderLeg(9)}
			{this.renderSpiderLeg(10)}
			{this.renderSpiderLeg(11)}
			{this.renderSpiderLeg(12)}
			</div>
			</div>
		)
