import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';

class WholeSite extends React.Component {
	render() {
		return (
			<MainArtistForm />
			//<SpotifyLogin />
		);
	}
}

const stateKey = "spotify_auth_state";
class SpotifyLogin extends React.Component {
  componentDidMount() {
    localStorage.removeItem(stateKey);
  }

  handleClick = () => {
    const client_id = "8f8783f95ca7449fbb4bb5ac6f84840b";
    const redirect_uri = "http://localhost:3000";
//    const scope = "user-read-private user-read-email";
    const state = generateRandomString(16);

    localStorage.setItem(stateKey, state);

    const url = `https://accounts.spotify.com/authorize?response_type=token&client_id=${encodeURIComponent(client_id)}&redirect_uri=${encodeURIComponent(redirect_uri)}&state=${encodeURIComponent(state)}`;

    window.location = url;
  };

  render() {
    return <button onClick={this.handleClick}>Log in</button>;
  }
};

class MainArtistForm extends React.Component {
	constructor(props) {
		super(props);
		this.state = {value: ''};
		this.handleSubmit = this.handleSubmit.bind(this);
		this.handleChange = this.handleChange.bind(this);
  }
  
  handleSubmit(event) {
	alert(this.state.value);
    event.preventDefault();
	getAPIToken();
  }
  
  handleChange(event) {
    this.setState({value: event.target.value});
  }
	render() {
		return (
			<div className="mainArtist">
				<form onSubmit={this.handleSubmit}>
					<input className="mainArtistInput"
					value={this.state.value} 
					type="text"
					onChange={this.handleChange}					/>
					<input type="submit" value="Submit" />
				</form>
			</div>
		);
	}
}

function getAPIToken() {
	fetch("https://api.spotify.com/v1/search?q=eevee&type=artist", {
		method: 'post',
		headers: {
			'Authorization': 'Bearer ' + getHashParam("access_token")
		},
		body: 'grant_type=implicit_grant',
		json: true
	}).then(response => response.json()).then(response => {console.log(response)});
}

function generateRandomString(length) {
  let text = "";
  const possible =
    "ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789";

  while (text.length <= length) {
    text += possible.charAt(Math.floor(Math.random() * possible.length));
  }

  return text;
};

function getHashParam(param) {
	var hash = window.location.hash.substr(1);

	var result = hash.split('&').reduce(function (result, item) {
		var parts = item.split('=');
		result[parts[0]] = parts[1];
		return result;
	}, {});
	return result[param]
};

ReactDOM.render(
  <WholeSite />,
  document.getElementById('root')
);
