import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';

class WholeSite extends React.Component {
	render() {
		return (
			<React.Fragment>
				<div id="spotifyLogin">
					<SpotifyLogin />
				</div>
				<div id="playlistBuilder">
					<PlaylistBuilder />
				</div>
			</React.Fragment>
			//<SpotifyLogin />
		);
	}
}

class PlaylistBuilder extends React.Component {
	constructor(props) {
		super(props);
		this.state = {mainArtistTracks:[],
					mainArtistSimilarArtists:[],
					mainArtist:"",
					};
		this.mainArtistChange = this.mainArtistChange.bind(this);
		this.mainArtistSubmit = this.mainArtistSubmit.bind(this);
		this.searchAPI = this.searchAPI.bind(this);
		this.artistTopTracksAPI = this.artistTopTracksAPI.bind(this);
		this.artistSimilarArtistsAPI = this.artistSimilarArtistsAPI.bind(this);
		this.artistGetAllData = this.artistGetAllData.bind(this);
	}
	
	mainArtistChange(mainArtistVal) {
		this.setState({mainArtist: mainArtistVal});
	}
	
	mainArtistSubmit(mainArtistVal) {
		this.searchAPI(this.state.mainArtist)
	}
	
	searchAPI(searchTerm) {
		fetch("https://api.spotify.com/v1/search?q="+encodeURIComponent(searchTerm)+"&type=artist&limit=1", {
			method: 'get',
			headers: {
				'Authorization': 'Bearer ' + getHashParam("access_token")
			},
			json: true
		}).then(response => response.json()).then(response => {this.artistGetAllData(response["artists"]["items"][0]["id"]);});
	}
	
	artistSimilarArtistsAPI(id) {
		fetch("https://api.spotify.com/v1/artists/"+encodeURIComponent(id)+"/top-tracks?market=from_token", {
			method: 'get',
			headers: {
				'Authorization': 'Bearer ' + getHashParam("access_token")
			},
			json: true
		}).then(response => response.json()).then(response => {this.setState({mainArtistTracks: response["tracks"]});});
	}
	
	artistTopTracksAPI(id) {
		fetch("https://api.spotify.com/v1/artists/"+encodeURIComponent(id)+"/top-tracks?market=from_token", {
			method: 'get',
			headers: {
				'Authorization': 'Bearer ' + getHashParam("access_token")
			},
			json: true
		}).then(response => response.json()).then(response => {this.setState({mainArtistTracks: response["tracks"]});});
	}
	
	render() {
		const mainArtist = this.state.mainArtist;
		const mainArtistTracks = this.state.mainArtistTracks;
		
		return (
			<MainArtistForm mainArtist={mainArtist} 
			mainArtistTracks={mainArtistTracks}
			mainArtistChange={this.mainArtistChange}
			mainArtistSubmit={this.mainArtistSubmit}
			/>
		)
	}
}

class MainArtistForm extends React.Component {
	constructor(props) {
		super(props);
		this.handleSubmit = this.handleSubmit.bind(this);
		this.handleChange = this.handleChange.bind(this);
  }
  
  handleSubmit(event) {
    event.preventDefault();
	this.props.mainArtistSubmit(this.props.mainArtist);
  }
  
  handleChange(event) {
    this.props.mainArtistChange(event.target.value);
  }
	
	render() {
		return (
			<div id="mainArtist">
				<form onSubmit={this.handleSubmit}>
					<input id="mainArtistInput"
					value={this.props.mainArtist} 
					type="text"
					onChange={this.handleChange} />
					<div id="mainArtistTracks">
						<ArtistSongList mainArtistTracks={this.props.mainArtistTracks} />
					</div>
					<input type="submit" value="Submit" />
				</form>
			</div>
		);
	}
}



function ArtistSongList(props) {
	return (
         <div>
            <table id='mainArtistTrackTable'>
				<colgroup>
				<col className="mainArtistSongTitleColumn" />
				<col className="mainArtistButtonColumn" />
				</colgroup>
				<tbody>
					{props.mainArtistTracks.map(mainArtistTracks => <tr key={mainArtistTracks["id"]} className="mainArtistTrackTableRow"><td className="mainArtistTrackTableCellName">{mainArtistTracks["name"]}</td><td><button className="mainArtistButton">Add</button></td></tr>)}
				</tbody>
            </table>
         </div>
	)
}



const stateKey = "spotify_auth_state";
class SpotifyLogin extends React.Component {
  componentDidMount() {
    localStorage.removeItem(stateKey);
  }

  handleClick = () => {
    const client_id = "8f8783f95ca7449fbb4bb5ac6f84840b";
    const redirect_uri = "http://localhost:3000";
//    const scope = "user-read-private user-read-email";
    const state = generateRandomString(16);

    localStorage.setItem(stateKey, state);

    const url = `https://accounts.spotify.com/authorize?response_type=token&client_id=${encodeURIComponent(client_id)}&redirect_uri=${encodeURIComponent(redirect_uri)}&state=${encodeURIComponent(state)}`;

    window.location = url;
  };

  render() {
    return <button onClick={this.handleClick}>Log in</button>;
  }
};

function generateRandomString(length) {
  let text = "";
  const possible =
    "ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789";

  while (text.length <= length) {
    text += possible.charAt(Math.floor(Math.random() * possible.length));
  }

  return text;
};

function getHashParam(param) {
	var hash = window.location.hash.substr(1);

	var result = hash.split('&').reduce(function (result, item) {
		var parts = item.split('=');
		result[parts[0]] = parts[1];
		return result;
	}, {});
	return result[param]
};

ReactDOM.render(
  <WholeSite />,
  document.getElementById('root')
);
