import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';

class WholeSite extends React.Component {
	render() {
		return (
			<div>
			<WholeSite />
			<SpotifyLogin />
			</div>
		);
	}
}

const stateKey = "spotify_auth_state";
class SpotifyLogin extends React.Component {
  componentDidMount() {
    localStorage.removeItem(stateKey);
  }

  handleClick = () => {
    const client_id = "8f8783f95ca7449fbb4bb5ac6f84840b";
    const redirect_uri = "http://localhost:3000";
    const scope = "user-read-private user-read-email";
    const state = generateRandomString(16);

    localStorage.setItem(stateKey, state);

    const url = `https://accounts.spotify.com/authorize?response_type=token&client_id=${encodeURIComponent(client_id)}&scope=${encodeURIComponent(scope)}&redirect_uri=${encodeURIComponent(redirect_uri)}&state=${encodeURIComponent(state)}`;

    window.location = url;
  };

  render() {
    return <button onClick={this.handleClick}>Log in</button>;
  }
};

class MainArtistForm extends React.Component {
	constructor(props) {
		super(props);
		this.state = {value: ''};
		this.handleSubmit = this.handleSubmit.bind(this);
		this.handleChange = this.handleChange.bind(this);
  }
  
  handleSubmit(event) {
	alert(this.state.value);
    event.preventDefault();
	SpotifyLogin();
  }
  
  handleChange(event) {
    this.setState({value: event.target.value});
  }
	render() {
		return (
			<div className="mainArtist">
				<form onSubmit={this.handleSubmit}>
					<input className="mainArtistInput"
					value={this.state.value} 
					type="text"
					onChange={this.handleChange}					/>
					<input type="submit" value="Submit" />
				</form>
			</div>
		);
	}
}

function getAPIToken() {
	fetch("https://accounts.spotify.com/api/token", {
		method: 'post',
		headers: new Headers({
			'Authorization': 'Basic '+btoa('8f8783f95ca7449fbb4bb5ac6f84840b:0f7aedbc71a14f83a5e255638f54b467')
		}),
		body: 'grant_type=client_credentials'
	}).then(response => response.json()).then(response => {console.log(response)});
}

function generateRandomString(length) {
  let text = "";
  const possible =
    "ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789";

  while (text.length <= length) {
    text += possible.charAt(Math.floor(Math.random() * possible.length));
  }

  return text;
};
